  def most_short
    puts "Celui qui a la plus petite, c'est #{@journalist.min_by(&:length)}.. désolé gros"
  end
  
  
  def five_caracter
        puts "Dans le tableau, il y a #{@journalist.count {|c| c.length == 6}} comptes avec 5 caractères"
  end 
  
  
  
  # Ca c'est ok !
  def uppercase_journalist
    puts "Il y a #{@journalist.count{|u| u =~ /^@[[:upper:]]/}} handle qui commencent par une majuscule."
  end
  
  
  # Ca c'est ok !
  def alphabetic_journalist
    puts "Classer les noms par ordre alphabétique #{@journalist.sort_by(&:downcase)}}"
  end
  
  
  # Ca c'est ok ! 
  def sort_by_size
    puts "Voici les comptes par ordre de grandeur du pseudo #{@journalist.sort_by(&:length)}"
  end
  
  
  # Ca c'est ok ! 
  def where_is_epenser()
    puts "@Epenser est à la place #{@journalist.find_index("@epenser")} dans le tableau"
  end
  
  
  
  def by_caracters 
   hand_size = Array.new 
   @journalist.each do |handle|
    hand_size << handle.length
   end
   for i in hand_size.min..hand_size.max
    puts "Il y a #{hand_size.count(i)} handles qui possèdent #{i-1} caractères."
   end
  
  end
  

